mapper.select:
select bno, title, writer, text, date,replyCnt,hit,cate,pin
	from (
		select @rownum:=@rownum+1 as rownum, b.* 
		from (
			select @rownum:=0
			) as tmp, board as b
		order by bno desc
	) as boardlist	
	
		where rownum > (#{pageNum} - 1) * #{amount} and rownum <= #{pageNum} * #{amount}
	
	 and pin=0
	
			
			and title like concat('%', #{keyword}, '%')  	
		
		
			and text like concat('%', #{keyword}, '%')
		
		
			and writer like concat('%', #{keyword}, '%')

select bno, title, writer, text, date,replyCnt,hit,cate,pin
	from (
		select @rownum:=@rownum+1 as rownum, b.* 
		from (
			select @rownum:=0
			) as tmp, board as b
		order by date desc, pin desc limit 3
	) as boardlist	
		where pin=1
	
			
			and title like concat('%', #{keyword}, '%')  	
		
		
			and text like concat('%', #{keyword}, '%')
		
		
			and writer like concat('%', #{keyword}, '%')	
		
	
	order by date desc

	select  bno, title, writer, text, date,replyCnt,hit,cate,pin
		from (
			select @rownum:=@rownum+1 as rownum, b.* 
			from (
				select @rownum:=0
				) as tmp, board as b
			order by bno desc
		) as boardlist	
		where cate = #{cate}
		and pin=0
		
 			and rownum > (#{pageNum} - 1) * #{amount} and rownum <= #{pageNum} * #{amount}
		 
		
				
			and title like concat('%', #{keyword}, '%') 	
			
			
				and text like concat('%', #{keyword}, '%')
			
			
				and writer like concat('%', #{keyword}, '%')

	select  bno, title, writer, text, date,replyCnt,hit,cate,pin
		from (
			select @rownum:=@rownum+1 as rownum, b.* 
			from (
				select @rownum:=0
				) as tmp, board as b
			order by date desc, pin desc limit 3
		) as boardlist	

		where pin=1
		
				
			and title like concat('%', #{keyword}, '%') 	
			
			
				and text like concat('%', #{keyword}, '%')
			
			
				and writer like concat('%', #{keyword}, '%')	
			
		
		order by date desc

mapper.update:
update board
set title=#{title},
	text=#{text}
where bno=#{bno}

update board
set replycnt = replycnt + #{amount}
where bno = #{bno}
