mapper.select:
select bno, title, writer, text, date,replyCnt,hit,cate
	from (
		select @rownum:=@rownum+1 as rownum, b.* 
		from (
			select @rownum:=0
			) as tmp, board as b
		order by bno desc
	) as boardlist	
	
		where rownum > (#{pageNum} - 1) * #{amount} and rownum <= #{pageNum} * #{amount}
	
	
			
			and title like concat('%', #{keyword}, '%')  	
		
		
			and text like concat('%', #{keyword}, '%')
		
		
			and writer like concat('%', #{keyword}, '%')

	select  bno, title, writer, text, date,replyCnt,hit,cate
		from (
			select @rownum:=@rownum+1 as rownum, b.* 
			from (
				select @rownum:=0
				) as tmp, board as b
			order by bno desc
		) as boardlist	
		where cate = #{cate}
		
 			and rownum > (#{pageNum} - 1) * #{amount} and rownum <= #{pageNum} * #{amount}
		 
		
				
			and title like concat('%', #{keyword}, '%') 	
			
			
				and text like concat('%', #{keyword}, '%')
			
			
				and writer like concat('%', #{keyword}, '%')

mapper.update:
update board
set title=#{title},
	text=#{text},
	thumbnail=#{thumbnail}
where bno=#{bno}

update board
set replycnt = replycnt + #{amount} 
where bno = #{bno}
