mapper.select:
		select * from (select @rownum:=@rownum+1 as rownum, b.*
		        from (
					select @rownum:=0
		            ) as tmp, reply as b
		            where bno = #{bno}						
			) as boardList
			
		    where rownum > (#{cri.pageNum} - 1) * #{cri.amount} and rownum <= #{cri.pageNum} * #{cri.amount}
		    
            order by if(reparent=0, rno, reparent),replyDate
